import React, { useEffect, useState } from "react";
import "../resource/styles/message.css";
import search from "../resource/svg/chat/message/search.svg";
import personAdd from "../resource/svg/chat/message/personadd.svg";
import groupAdd from "../resource/svg/chat/message/groupadd.svg";
import expandMore from "../resource/svg/chat/message/expandMore.svg";
import more from "../resource/svg/chat/message/more.svg";
import user from "../resource/img/message/nu9.png";
import DetailMessageUser from "./DetailMessageUser";
import axios from "axios";

function Message() {
  const [textSearch, setTextSearch] = useState("");
  const [showAllMessgae, setShowAllMessage] = useState(true);
  const [maxHeight, setMaxHeight] = useState("");
  const [sessionToken, setSessionToken] = useState("");

  useEffect(() => {
    setMaxHeight(window.innerHeight);
  }, []);

  useEffect(() => {
    const getSessionToken = async () => {
      const sessionToken_data = JSON.parse(
        localStorage.getItem("sessiontoken")
      );
      setSessionToken(sessionToken_data);
    };
    getSessionToken();
  });

  const data_user = {
    avatar: user,
    username: "Kim Young Jung",
    latestMessage: "Chang quy ve si",
    countMessage: 3,
    lastOnline: "3 giờ",
  };
  const numberOfUsers = 50;
  const data_users = Array.from({ length: numberOfUsers }, () => data_user);

  const handleChangeTextSearch = (e) => {
    setTextSearch(e.target.value);
  };
  const handleShowAllMessage = (result) => {
    setShowAllMessage(result);
  };

  return (
    <>
      <div className="message-contains-infor flex">
        <div className="message-left-bar">
          <div className="flex">
            <div className="message-nav-bar">
              <div className="message-search-tool flex">
                <div className="message-search-member flex">
                  <img src={search} alt="search-icon" />
                  <input
                    type="text"
                    value={textSearch}
                    onChange={handleChangeTextSearch}
                    placeholder="Tìm kiếm"
                  />
                </div>
                <div className="message-add-members flex">
                  <div style={{ margin: "5px" }}>
                    <img src={personAdd} alt="person add" />
                  </div>
                  <div>
                    <img src={groupAdd} alt="group add" />
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="message-container-chat">
            <div className="message-container-controll flex">
              <div className="message-left-controll flex">
                <p
                  onClick={() => handleShowAllMessage(true)}
                  className={`${
                    showAllMessgae ? "message-left-controll-active" : ""
                  }`}
                >
                  Tất cả
                </p>
                <p
                  className={`${
                    showAllMessgae ? "" : "message-left-controll-active"
                  }`}
                  onClick={() => handleShowAllMessage(false)}
                >
                  Chưa đọc
                </p>
              </div>
              <div className="message-right-controll flex">
                <div className="message-classification-more flex">
                  <p>Phân loại</p>
                  <img src={expandMore} alt="classification more" />
                </div>
                <div className="message-btn-more">
                  <img src={more} alt="them" />
                </div>
              </div>
              <hr
                className={`message-hr-transaction ${
                  showAllMessgae ? "" : "message-hr-active"
                }`}
              />
            </div>
            <div
              className="message-list-chat"
              style={{ maxHeight: `${maxHeight}px` }}
            >
              {data_users.map((data, index) => (
                <div className="message-detail-list-chat flex" key={index}>
                  <div className="flex">
                    <div>
                      <img src={data.avatar} alt="avatar user" />
                    </div>
                    <div className="message-message-title">
                      <h5>{data.username}</h5>
                      <p>{`${data.latestMessage}`}</p>
                    </div>
                  </div>
                  <div className="message-message-last-date">
                    <p className="message-last-date">{data.lastOnline}</p>
                    <div className="flex">
                      <p className="message-count-message">
                        {data.countMessage}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
        <div className="message-right-bar background-message">
          <DetailMessageUser />
        </div>
      </div>
    </>
  );
}

export default Message;
