import React, { useState } from "react";
import "../../resource/styles/phoneBook.css";
import SearchComponent from "./SearchComponent";
import expanMore from "../../resource/svg/chat/message/expandMore.svg";
import person from "../../resource/svg/chat/phoneBook/person.svg";
import group from "../../resource/svg/chat/phoneBook/group.svg";
import email from "../../resource/svg/chat/phoneBook/email.svg";

function PhoneBook() {
  const [indexComponent, setIndexCompoent] = useState(0);

  const listComponent = [];
  const ShowComponent = listComponent[indexComponent];

  const listOptions = [
    {
      title: "Danh sách bạn bè",
      img: person,
    },
    {
      title: "Danh sách nhóm",
      img: group,
    },
    {
      title: "Lời mời kết bạn",
    },
  ];

  const handleShowComponent = () => {
    setIndexCompoent;
  };
  return (
    <>
      <div className="container-infor">
        <div className="left-bar">
          <SearchComponent />
          <div className="group-search">
            {listOptions.map((option, index) => (
              <div
                key={index}
                onClick={() => handleShowComponent(index)}
                className="list-property flex"
              >
                <img src={option.img} alt={option.title} />
                <p>{option.title}</p>
              </div>
            ))}
            <div
              onClick={handleShowComponent}
              className="list-property list-friend flex"
            >
              <img src={person} alt="" />
              <p>Danh sách bạn bè</p>
            </div>
            <div
              onClick={handleShowComponent}
              className="list-property list-group flex"
            >
              <img src={group} alt="" />
              <p>Danh sách nhóm</p>
            </div>
            <div
              onClick={handleShowComponent}
              className="list-property friend-request flex"
            >
              <img src={email} alt="" />
              <p>Lời mời kết bạn</p>
            </div>
          </div>
        </div>
        <div className="right-bar">
          <div className="detail-user-property">
            <ShowComponent />
          </div>
        </div>
      </div>
    </>
  );
}

export default PhoneBook;
