import React, { useState } from "react";
import "../../resource/styles/phoneBook.css";
import SearchComponent from "./SearchComponent";
import search from "../../resource/svg/chat/message/search.svg";

import expanMore from "../../resource/svg/chat/message/expandMore.svg";
import person from "../../resource/svg/chat/phoneBook/person.svg";
import group from "../../resource/svg/chat/phoneBook/group.svg";
import email from "../../resource/svg/chat/phoneBook/email.svg";

function PhoneBook() {
  const [indexComponent, setIndexCompoent] = useState(0);

  // const listComponent = [];
  // const ShowComponent = listComponent[indexComponent];

  const listOptions = [
    {
      title: "Danh sách bạn bè",
      img: person,
    },
    {
      title: "Danh sách nhóm",
      img: group,
    },
    {
      title: "Lời mời kết bạn",
      img: email,
    },
  ];

  const handleShowComponent = (index) => {
    setIndexCompoent(index);
  };
  console.log(indexComponent);
  return (
    <>
      <div className="container-infor flex">
        <div className="left-bar">
          <SearchComponent />
          <div className="group-search">
            {listOptions.map((option, index) => (
              <div
                key={index}
                onClick={() => handleShowComponent(index)}
                className="list-property flex"
              >
                <img src={option.img} alt={option.title} />
                <p className="w500">{option.title}</p>
              </div>
            ))}
          </div>
        </div>
        <div className="right-bar">
          <div className="detail-user-property">
            {/* <ShowComponent /> */}
            <ContainerOptions />
          </div>
        </div>
      </div>
    </>
  );
}

function ContainerOptions() {
  return (
    <>
      <div className="list-property title-options flex">
        <img src={person} alt="" />
        <p className="w500">Danh sách bạn bè</p>
      </div>
      <div className="wrap-list-user">
        <div className="count-list">
          <p className="w500">Bạn bè (64)</p>
        </div>
        <div className="detail-list-user">
          <TaskFriend />
        </div>
      </div>
    </>
  );
}

function TaskFriend() {
  const [textSearch, setTextSearch] = useState("");

  const handleChangeTextSearch = (e) => {
    setTextSearch(e.target.value);
  };
  return (
    <>
      <div>
        <div className="flex">
          <div className="message-search-member flex">
            <img src={search} alt="search-icon" />
            <input
              type="text"
              value={textSearch}
              onChange={handleChangeTextSearch}
              placeholder="Tìm kiếm"
            />
          </div>
        </div>
      </div>
    </>
  );
}

function TaskGroup() {}

function TaskFriendRequest() {}

export default PhoneBook;
