import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../resource/styles/chat.css";
import avatar from "../resource/img/avatar.png";
import message from "../resource/svg/chat/message.svg";
import toDo from "../resource/svg/chat/toDo.svg";
import cloud from "../resource/svg/chat/cloud.svg";
import toolBox from "../resource/svg/chat/toolBox.svg";
import setting from "../resource/svg/chat/setting.svg";
import addressBook from "../resource/svg/chat/addressBook.svg";
import Cloud from "../component/Cloud";
import Message from "../component/Message";
import PhoneBook from "../component/PhoneBook";
import Setting from "../component/Setting";
import ToDo from "../component/ToDo";
import ToolBox from "../component/ToolBox";

function Chat() {
  const [menuActive, setMenuActive] = useState("");
  const [indexConponent, setIndexConponent] = useState(0);
  const navigator = useNavigate();
  const topMenu = [
    {
      id: "icon-message",
      value: message,
    },
    {
      id: "icon-address",
      value: addressBook,
    },
    {
      id: "icon-todo",
      value: toDo,
    },
  ];
  const bottomMenu = [
    {
      id: "icon-cloud",
      value: cloud,
    },
    {
      id: "icon-toolBox",
      value: toolBox,
    },
    {
      id: "icon-setting",
      value: setting,
    },
  ];
  const component = [Message, PhoneBook, ToDo, Cloud, ToolBox, Setting];
  const SelectComponent = component[indexConponent];
  const data_user = {
    avatar: avatar,
    username: "Kim Song Kang",
  };

  useEffect(() => {
    const fetch = async () => {
      var account = localStorage.getItem("account");
      var password = localStorage.getItem("password");

      if (account === "qqqqqq" && password === "qqqqqq") {
        navigator("/");
      } else {
        navigator("/login");
      }
    };
    fetch();
  }, []);

  function handleMenuActive(className, index) {
    setMenuActive(className);
    setIndexConponent(index);
  }

  return (
    <>
      <div className="flex">
        <div className="chat-menu">
          <div className="chat-img-avatar">
            <img src={data_user.avatar} alt="" />
            <div className="box-controll-account">
              <p>{data_user.username}</p>
            </div>
          </div>
          <div className="chat-menu-top">
            {topMenu.map((item, index) => (
              <div
                key={item.id}
                className={`${item.id} ${
                  item.id === menuActive ? "chat-activeMenu" : ""
                }`}
                onClick={() => handleMenuActive(item.id, index)}
              >
                <img src={item.value} alt={item.id} />
              </div>
            ))}
          </div>
          <div className="chat-menu-bottom">
            {bottomMenu.map((item, index) => (
              <div
                key={item.id}
                className={`${item.id} ${
                  item.id === menuActive ? "chat-activeMenu" : ""
                }`}
                onClick={() => handleMenuActive(item.id, index + 3)}
              >
                <img src={item.value} alt={item.id} />
              </div>
            ))}
          </div>
        </div>

        <div className="chat-container">
          <SelectComponent />
        </div>
      </div>
    </>
  );
}

export default Chat;
