import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../resource/styles/chat.css";
import message from "../resource/svg/chat/message.svg";
import toDo from "../resource/svg/chat/toDo.svg";
import cloud from "../resource/svg/chat/cloud.svg";
import toolBox from "../resource/svg/chat/toolBox.svg";
import setting from "../resource/svg/chat/setting.svg";
import addressBook from "../resource/svg/chat/addressBook.svg";
import Cloud from "../component/Cloud";
import Message from "../component/Message";
import PhoneBook from "../component/PhoneBook";
import Setting from "../component/Setting";
import ToDo from "../component/ToDo";
import ToolBox from "../component/ToolBox";

function Chat() {
  const [menuActive, setMenuActive] = useState("");
  const [menuLogout, setMenuLogout] = useState("");
  const [indexConponent, setIndexConponent] = useState(0);
  const [loadScreen, setScreenLoad] = useState(true);
  const [sessionToken, setSessionToken] = useState("");
  const [currentUser, setCurrentUser] = useState("");
  const [allUsers, setAllUser] = useState(null);

  const navigate = useNavigate();
  const topMenu = [
    {
      id: "icon-message",
      value: message,
    },
    {
      id: "icon-address",
      value: addressBook,
    },
    {
      id: "icon-todo",
      value: toDo,
    },
  ];
  const bottomMenu = [
    {
      id: "icon-cloud",
      value: cloud,
    },
    {
      id: "icon-toolBox",
      value: toolBox,
    },
    {
      id: "icon-setting",
      value: setting,
    },
  ];
  const component = [Message, PhoneBook, ToDo, Cloud, ToolBox, Setting];
  const SelectComponent = component[indexConponent];
  const data_user = {
    avatar: JSON.parse(localStorage.getItem("avatar")),
    username: JSON.parse(localStorage.getItem("username")),
  };

  useEffect(() => {
    const fetch = async () => {
      const data = {
        sessiontoken: await JSON.parse(localStorage.getItem("sessiontoken")),
      };
      if (data.sessiontoken == null) {
        navigate("/login");
        return;
      }
      await axios
        .post("http://127.0.0.1:8080/auth/sessiontoken", data)
        .then((response) => {
          if (response.status === 200) {
            const sessionToken_data = JSON.stringify(response.data);
            localStorage.setItem("sessiontoken", sessionToken_data);
            setSessionToken(sessionToken_data);
            setScreenLoad(false);
          }
        })
        .catch(() => {
          setScreenLoad(false);
          navigate("/login");
        });
    };
    fetch();
  }, []);

  useEffect(() => {
    const getAllUser = async () => {
      await axios
        .post("http://127.0.0.1:8080/user/getfriend")
        .then((response) => {
          if (response.status === 200) {
            setAllUser(response.data);
          }
        })
        .catch((err) => {
          console.error(err);
        });
    };
    getAllUser();
    console.log(allUsers);
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };
  const handleShowMenuLogout = () => {
    menuLogout ? setMenuLogout(false) : setMenuLogout(true);
  };

  function handleMenuActive(className, index) {
    setMenuActive(className);
    setIndexConponent(index);
  }

  return (
    <>
      <div style={{ display: loadScreen ? "block" : "none" }}>
        <LoadData />
      </div>
      <div className="flex" style={{ display: loadScreen ? "none" : "flex" }}>
        <div className="chat-menu">
          <div className="chat-img-avatar">
            <img onClick={handleShowMenuLogout} src={data_user.avatar} alt="" />
            <div
              className={`box-controll-account ${
                menuLogout ? "box-controll-account-active" : ""
              }`}
            >
              <h3>{data_user.username}</h3>
              <div>
                <p>Hồ sơ của bạn</p>
                <p>Cài đặt</p>
              </div>
              <p className="btn-logout" onClick={handleLogout}>
                Đăng suất
              </p>
            </div>
          </div>
          <div className="chat-menu-top">
            {topMenu.map((item, index) => (
              <div
                key={item.id}
                className={`${item.id} ${
                  item.id === menuActive ? "chat-activeMenu" : ""
                }`}
                onClick={() => handleMenuActive(item.id, index)}
              >
                <img src={item.value} alt={item.id} />
              </div>
            ))}
          </div>
          <div className="chat-menu-bottom">
            {bottomMenu.map((item, index) => (
              <div
                key={item.id}
                className={`${item.id} ${
                  item.id === menuActive ? "chat-activeMenu" : ""
                }`}
                onClick={() => handleMenuActive(item.id, index + 3)}
              >
                <img src={item.value} alt={item.id} />
              </div>
            ))}
          </div>
        </div>

        <div className="chat-container">
          <SelectComponent />
        </div>
      </div>
    </>
  );
}

function LoadData() {
  return (
    <>
      <div
        className="load_login flex"
        style={{
          justifyContent: "center",
          alignContent: "center",
          alignItems: "center",
          margin: "auto",
        }}
      >
        <div>
          <div className="bar">
            <h1
              style={{ fontSize: "50px", padding: "100px" }}
              className="textcenter"
            >
              Zalo
            </h1>
            <p style={{ textAlign: "center", color: "rgb(27 119 225)" }}>
              Đang đăng nhập . . .
            </p>
          </div>
        </div>
      </div>
    </>
  );
}
export default Chat;
