import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../resource/styles/chat.css";
import avatar from "../resource/img/avatar.png";
import Message from "../resource/svg/chat/message.svg";
import toDo from "../resource/svg/chat/toDo.svg";
import cloud from "../resource/svg/chat/cloud.svg";
import toolBox from "../resource/svg/chat/toolBox.svg";
import setting from "../resource/svg/chat/setting.svg";
import addressBook from "../resource/svg/chat/addressBook.svg";

function Chat() {
  const [menuActive, setMenuActive] = useState("");
  const navigator = useNavigate();
  const topMenu = [Message, addressBook, toDo];
  const bottomMenu = [cloud, toolBox, setting];

  useEffect(() => {
    const fetch = async () => {
      var account = localStorage.getItem("account");
      var password = localStorage.getItem("password");

      if (account === "qqqqqq" && password === "qqqqqq") {
        navigator("/");
      } else {
        navigator("/login");
      }
    };
    fetch();
  }, []);

  function handleMenuActive(e) {
    setMenuActive(e.currentTarget.className);
    console.log(menuActive);
  }

  return (
    <>
      <div className="flex">
        <div className="menu">
          <div className="img-avatar">
            <img src={avatar} alt="" />
          </div>
          <div className="menu-top">
            {topMenu.map((item) => (
              <div className={`icon-${item}`}></div>
            ))}
          </div>
          <div className="menu-bottom">
            <div className="icon-cloud">
              <img src={cloud} alt="" />
            </div>
            <div className="icon-toolBox">
              <img src={toolBox} alt="" />
            </div>
            <div className="icon-setting">
              <img src={setting} alt="" />
            </div>
          </div>
        </div>

        <div className="container"></div>
      </div>
    </>
  );
}

export default Chat;
