import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import qr from "../resource/img/qr.png";
import iphone from "../resource/svg/iphone.svg";
import lock from "../resource/svg/lock.svg";
import "../resource/styles/login.css";
import axios from "axios";

function Login() {
  const [sdt, setSdt] = useState("");
  const [pass, setPass] = useState("");
  const [disableBtn, setDisableBtn] = useState(true);
  const [showComponent, setShowComponent] = useState(true);

  const btnLogin = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetch = async () => {
      console.log("has");
      const sessiontoken = await JSON.parse(
        localStorage.getItem("sessiontoken")
      );
      if (!sessiontoken) {
        return;
      } else {
        const data = {
          sessiontoken,
        };
        await axios
          .post("http://127.0.0.1/auth/sessiontoken", data)
          .then((response) => {
            if (response.status === 200) {
              localStorage.setItem(
                "sessiontoken",
                JSON.stringify(response.data)
              );
              navigate("/");
            } else {
              navigate("/login");
            }
          })
          .catch((err) => {
            console.error(err);
            navigate("/login");
          });
      }
    };
    fetch();
  }, []);

  const handleSubmitLogin = async (e) => {
    e.preventDefault();
    const data = {
      phone: sdt,
      password: pass,
    };

    await axios
      .post("http://127.0.0.1:8080/auth/login", data)
      .then((response) => {
        if (response.status === 200) {
          localStorage.setItem("sessiontoken", JSON.stringify(response.data));
          navigate("/");
        }
      });
  };

  useEffect(() => {
    if (sdt.length >= 4 && pass.length >= 4) {
      setDisableBtn(false);
      btnLogin.current.focus();
    } else {
      setDisableBtn(true);
    }
  }, [sdt, pass]);
  const handleChangeSDT = (e) => {
    setSdt(e.target.value);
  };
  const handleChangePass = (e) => {
    setPass(e.target.value);
  };
  const handleDisplay = (value) => {
    setShowComponent(value);
  };

  return (
    <>
      <div>
        <div className="bar">
          <div>
            <h1 style={{ fontSize: "50px" }} className="textcenter">
              Zalo
            </h1>
          </div>
          <div className="title-zalo">
            <p className="textcenter">Đăng nhập với tài khoản Zalo</p>
            <p className="textcenter">để kết nối với ứng dụng Zalo Web</p>
          </div>
        </div>
        <div
          className="wrap-qr-code"
          style={{ display: "block", margin: "auto" }}
        >
          <div className="navbar-qr-code">
            <div className="relative-hr">
              <div className="flex title-qr">
                <div onClick={() => handleDisplay(true)}>
                  <span
                    className={`btn-qr ${
                      showComponent ? "login-show-title-active" : ""
                    }`}
                  >
                    VỚI MÃ QR
                  </span>
                </div>
                <hr
                  style={{
                    display: "block",
                    margin: "auto",
                    height: "20px",
                    opacity: 0.5,
                  }}
                />
                <div onClick={() => handleDisplay(false)}>
                  <span
                    className={`btn-sdt ${
                      showComponent ? "" : "login-show-title-active"
                    }`}
                  >
                    VỚI SỐ ĐIỆN THOẠI
                  </span>
                </div>
              </div>
              <hr
                className={`hr-tonggle ${
                  showComponent ? "" : "login-show-hr-active"
                }`}
              />
            </div>

            <div
              className={`display-qr ${
                showComponent ? "login-show-componet-active" : ""
              }`}
            >
              <div className="qr-code">
                <div className="outline-qr-code">
                  <img src={qr} alt="qr-code" />
                  <p className="textcenter" style={{ color: "blue" }}>
                    Chỉ dùng để đăng nhập
                  </p>
                  <p className="textcenter">Zalo trên máy tính</p>
                </div>
              </div>
              <div className="title-footer textcenter">
                Sử dụng Zalo để quét mã QR
              </div>
            </div>

            <div
              className={`display-sdt ${
                showComponent ? "" : "login-show-componet-active"
              }`}
            >
              <div>
                <div className="login-account">
                  <div className="flex">
                    <img src={iphone} alt="" />
                    <input
                      type="text"
                      value={sdt}
                      onChange={handleChangeSDT}
                      placeholder="Số điện thoai"
                    />
                  </div>
                  <hr
                    style={{
                      margin: "3px auto",
                      width: "350px",
                      opacity: 0.3,
                    }}
                  />
                  <div className="flex">
                    <img src={lock} alt="" style={{ opacity: 0.6 }} />
                    <input
                      type="text"
                      value={pass}
                      onChange={handleChangePass}
                      placeholder="Mật khẩu"
                    />
                  </div>
                  <hr className="hr-title-qr" />
                </div>

                <div
                  className={`btn-help-login ${
                    disableBtn ? "" : "btn-help-login-active"
                  }`}
                >
                  <div className="login-on-pass">
                    <button
                      ref={btnLogin}
                      onClick={handleSubmitLogin}
                      className={`${disableBtn ? "" : "btn-login-active"}`}
                    >
                      Đăng nhập với mật khẩu
                    </button>
                  </div>
                  <div className="login-on-device">
                    <button
                      className={`${disableBtn ? "" : "btn-login-active"}`}
                    >
                      Đăng nhập bằng thiết bị di động
                    </button>
                    <div className="tam-giac"></div>
                    <span>Đăng nhập không dùng mật khẩu</span>
                  </div>
                  <hr className="hr-bottom-qr" />
                  <div>
                    <p
                      className={`forget-old-pass ${
                        disableBtn ? "" : "btn-login-active"
                      }`}
                    >
                      Quên mật khẩu?
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Login;
