function DetailMessageUser() {
  const data_user = {
    avatar: avatar,
    message: "Chang quy ve si za handle button melogin handle console.log",
    username: "Kim Young Jung",
    lastTime: "3 giờ",
    timmer: "21:09 11/12/2023",
    messageTime: "8:29",
  };
  const data_message = {
    avatar: avatar,
    message:
      "Chang quy ve si za handle button melogin handle button cddddddđole choice storage and backup soliuton",
    messageTime: "8:31",
  };
  const data_messages = Array.from({ length: 20 }, () => data_message);

  return (
    <>
      <div className="flex">
        <div className="contains-interface">
          <div className="header-container flex">
            <div className="left-header flex">
              <div className="flex">
                <img className="right-icon-arowback" src={arownBack} alt="" />
                <img
                  className="right-img-avatar"
                  src={data_user.avatar}
                  alt=""
                />
                <div className="user-infor">
                  <h5>{data_user.username}</h5>
                  <p>{`Truy cập ${data_user.lastTime} trước`}</p>
                </div>
              </div>
            </div>
            <div className="right-header flex">
              <img
                className="right-icon-group"
                src={addGroup}
                alt="add-group"
              />
              <img className="right-icon-search" src={search} alt="search" />
              <img className="right-icon-webcam" src={webcame} alt="webcam" />
            </div>
          </div>

          <div className="center-container">
            <ul>
              {data_messages.map((userMessage, index) => (
                <li key={index}>
                  <div>
                    <div className="contains-mess flex">
                      <div className="user-img-avatar">
                        <img src={userMessage.avatar} alt="" />
                      </div>
                      <div className="user-message-avatar">
                        <p className="message-detail">{userMessage.message}</p>
                        <p className="message-timer">
                          {userMessage.messageTime}
                        </p>
                      </div>
                    </div>
                    <div className="contains-mess my-message-text flex">
                      <div className="user-message-avatar">
                        <p className="message-detail">{userMessage.message}</p>
                        <p className="message-timer">
                          {userMessage.messageTime}
                        </p>
                      </div>
                    </div>
                  </div>
                </li>
              ))}
            </ul>
          </div>

          <div className="footer-container">
            <div className="upper-footer flex">
              <img src={sticker} alt="" />
              <img src={image} alt="" />
              <img src={attach} alt="" />
              <img src={screenCapture} alt="" />
              <img src={businessCard} alt="" />
              <img src={clock} alt="" />
              <img src={toDoMission} alt="" />
              <img src={chamthan} alt="" />
            </div>
            <div className="lower-footer flex">
              <input
                type="text"
                placeholder={`Nhap @, tin nhắn tới ${data_user.username}`}
              />
              <img src={fastMessage} alt="" />
              <img src={emotion} alt="" />
              <img src={like} alt="" />
            </div>
          </div>
        </div>
        <div className="conversation-infor">
          <div className="title-conversation">
            <div>
              <p className="tiltle-detail-infor">Thông tin hội thoại</p>
            </div>
          </div>
          <div className="scroll-infor-conversation">
            <div className="user-conversation-infor">
              <div className="conversation-infor-avatar">
                <img src={data_message.avatar} alt="" />
              </div>
              <div className="user-conversation-nickname">
                <span>{data_user.username}</span>
                <span>
                  <img src={penEditing} alt="" />
                </span>
              </div>
              <div className="user-conversation-option flex">
                <div>
                  <img src={bell} alt="" />
                  <p>Tắt thông báo</p>
                </div>
                <div>
                  <img src={pushPin} alt="" />
                  <p>Ghim hội thoại</p>
                </div>
                <div>
                  <img src={addGroup} alt="" />
                  <p>Tạo nhóm trò chuyện</p>
                </div>
              </div>
            </div>
            <div className="appointment-conversation">
              <div className="flex">
                <img src={clock} alt="" />
                <p>Danh sách nhắc hẹn</p>
              </div>
            </div>
            <div className="list-img-appointment">
              <ListImgAppointment />
            </div>
            <div className="list-file-appointment"></div>
            <div className="list-linked-appointment"></div>
            <div className="options-appointment"></div>
          </div>
        </div>
      </div>
    </>
  );
}

function ListImgAppointment() {
  const listImg = [
    anhchung1,
    anhchung2,
    anhchung3,
    anhchung4,
    anhchung5,
    anhchung6,
    anhchung7,
  ];
  const [showComponent, setShowComponent] = useState(true);

  const handleShowComponent = () => {
    showComponent ? setShowComponent(false) : setShowComponent(true);
  };

  return (
    <>
      <div>
        <div className="title-is-img flex">
          <p>Ảnh/Video</p>
          <img
            className={`${showComponent ? "" : "component-img-active"}`}
            onClick={handleShowComponent}
            src={tamgiac}
            alt=""
          />
        </div>
        <div
          className={`detial-list-img ${
            showComponent ? "" : "detial-list-img-active"
          }`}
        >
          <div className="show-some-img">
            <ul>
              <li className="flex">
                <img src={anhchung1} alt="" />
                <img src={anhchung2} alt="" />
                <img src={anhchung3} alt="" />
                <img src={anhchung4} alt="" />
              </li>
            </ul>
          </div>
          <div className="btn-show-all-img">
            <p>Xem tất cả</p>
          </div>
        </div>
      </div>
    </>
  );
}
