import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import qr from "../resource/img/qr.png";
import iphone from "../resource/svg/iphone.svg";
import lock from "../resource/svg/lock.svg";
import "../resource/styles/login.css";

function Login() {
  const [sdt, setSdt] = useState("");
  const [pass, setPass] = useState("");
  const [disableBtn, setDisableBtn] = useState(true);
  const [showComponent, setShowComponent] = useState(true);
  const [showTitle, setShowTitle] = useState(true);
  const [showHr, setShowHr] = useState(true);

  const btnLogin = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetch = () => {
      const account = localStorage.getItem("account");
      const pass = localStorage.getItem("password");
      if (account === "qqqqqq" && pass === "qqqqqq") {
        navigate("/");
      }
    };
    fetch();
  });

  useEffect(() => {
    var element = document.querySelector(".btn-help-login");
    if (sdt.length >= 6 && pass.length >= 6) {
      element.style.opacity = 1;
      setDisableBtn(false);
      btnLogin.current.focus();
    } else {
      element.style.opacity = 0.6;
      setDisableBtn(true);
    }
  }, [sdt, pass]);

  const handleChangeSDT = (e) => {
    setSdt(e.target.value);
  };
  const handleChangePass = (e) => {
    setPass(e.target.value);
  };
  const handleSubmitLogin = () => {
    const account = sdt;
    const password = pass;

    if (account === "qqqqqq" && password === "qqqqqq") {
      localStorage.setItem("account", account);
      localStorage.setItem("password", pass);
      navigate("/");
    }
  };

  const handleDisplay = (value) => {
    // var element_sdt = document.querySelector(".display-sdt");
    // var element_qr = document.querySelector(".display-qr");
    // document.querySelector(".btn-sdt").style.opacity = 0.5;
    // document.querySelector(".btn-qr").style.opacity = 1;
    // element_sdt.style.display = "none";
    // element_qr.style.display = "block";
    // document.querySelector(".hr-tonggle").style.left = "40px";
  };
  const handleDisplaySDT = () => {
    // var element_qr = document.querySelector(".display-qr");
    // var element_sdt = document.querySelector(".display-sdt");
    // document.querySelector(".btn-qr").style.opacity = 0.5;
    // document.querySelector(".btn-sdt").style.opacity = 1;
    // element_qr.style.display = "none";
    // element_sdt.style.display = "block";
    // document.querySelector(".hr-tonggle").style.left = "240px";
  };

  return (
    <>
      <div>
        <div className="bar">
          <div>
            <h1 style={{ fontSize: "50px" }} className="textcenter">
              Zalo
            </h1>
          </div>
          <div className="title-zalo">
            <p className="textcenter">Đăng nhập với tài khoản Zalo</p>
            <p className="textcenter">để kết nối với ứng dụng Zalo Web</p>
          </div>
        </div>
        <div
          className="wrap-qr-code"
          style={{ display: "block", margin: "auto" }}
        >
          <div className="navbar-qr-code">
            <div className="relative-hr">
              <div className="flex title-qr">
                <div onClick={() => handleDisplay(true)}>
                  <span
                    className={`btn-qr ${
                      showTitle ? "" : "login-show-componet-off"
                    }`}
                  >
                    VỚI MÃ QR
                  </span>
                </div>
                <hr
                  style={{
                    display: "block",
                    margin: "auto",
                    height: "20px",
                    opacity: 0.5,
                  }}
                />
                <div onClick={() => handleDisplay(false)}>
                  <span
                    className={`btn-sdt ${
                      showTitle ? "" : "login-show-componet-active"
                    }`}
                  >
                    VỚI SỐ ĐIỆN THOẠI
                  </span>
                </div>
              </div>
              <hr className="hr-tonggle" />
            </div>

            <div className="display-qr">
              <div className="qr-code">
                <div className="outline-qr-code">
                  <img src={qr} alt="qr-code" />
                  <p className="textcenter" style={{ color: "blue" }}>
                    Chỉ dùng để đăng nhập
                  </p>
                  <p className="textcenter">Zalo trên máy tính</p>
                </div>
              </div>
              <div className="title-footer textcenter">
                Sử dụng Zalo để quét mã QR
              </div>
            </div>

            <div
              className="display-sdt"
              style={{ display: "none", marginTop: "40px" }}
            >
              <div>
                <div className="login-account">
                  <div className="flex">
                    <img src={iphone} alt="" />
                    <input
                      type="text"
                      value={sdt}
                      onChange={handleChangeSDT}
                      placeholder="Số điện thoai"
                    />
                  </div>
                  <hr
                    style={{
                      margin: "3px auto",
                      width: "350px",
                      opacity: 0.3,
                    }}
                  />
                  <div className="flex">
                    <img src={lock} alt="" style={{ opacity: 0.6 }} />
                    <input
                      type="text"
                      value={pass}
                      onChange={handleChangePass}
                      placeholder="Mật khẩu"
                    />
                  </div>
                  <hr
                    style={{ margin: "3px auto", width: "350px", opacity: 0.3 }}
                  />
                </div>

                <div className="btn-help-login">
                  <div className="login-on-pass">
                    <button
                      ref={btnLogin}
                      onClick={handleSubmitLogin}
                      style={{
                        cursor: disableBtn ? "not-allowed" : "pointer",
                        pointerEvents: disableBtn ? "none" : "auto",
                      }}
                    >
                      Đăng nhập với mật khẩu
                    </button>
                  </div>
                  <div className="login-on-device">
                    <button
                      style={{
                        cursor: disableBtn ? "not-allowed" : "pointer",
                        pointerEvents: disableBtn ? "none" : "auto",
                      }}
                    >
                      Đăng nhập bằng thiết bị di động
                    </button>
                    <div className="tam-giac"></div>
                    <span>Đăng nhập không dùng mật khẩu</span>
                  </div>
                  <hr
                    style={{
                      margin: "40px auto 10px auto",
                      width: "350px",
                      opacity: 0.3,
                    }}
                  />
                  <div>
                    <p
                      style={{
                        margin: "15px auto",
                        textAlign: "center",
                        fontSize: "13px",
                        cursor: disableBtn ? "not-allowed" : "pointer",
                        pointerEvents: disableBtn ? "none" : "auto",
                      }}
                    >
                      Quên mật khẩu?
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Login;
