import React, { useState } from "react";
import "../resource/styles/message.css";
import search from "../resource/svg/chat/message/search.svg";
import personAdd from "../resource/svg/chat/message/personadd.svg";
import groupAdd from "../resource/svg/chat/message/groupadd.svg";
import expandMore from "../resource/svg/chat/message/expandMore.svg";
import more from "../resource/svg/chat/message/more.svg";
import user from "../resource/img/chat/message/nu9.png";

function Message() {
  const [textSearch, setTextSearch] = useState("");
  const [showAllMessgae, setShowAllMessage] = useState(true);

  const data = {
    avatar: user,
    username: "Kim Young Jung",
    latestMessage: "chang quy ve si",
    lastOnline: 3,
  };

  const handleChangeTextSearch = (e) => {
    setTextSearch(e.target.value);
  };
  const handleShowAllMessage = (result) => {
    setShowAllMessage(result);
  };

  return (
    <>
      <div className="flex">
        <div className="left-bar">
          <div className="flex">
            <div className="nav-bar">
              <div className="search-tool flex">
                <div className="search-member flex">
                  <img src={search} alt="search-icon" />
                  <input
                    type="text"
                    value={textSearch}
                    onChange={handleChangeTextSearch}
                    placeholder="Tìm kiếm"
                  />
                </div>
                <div className="add-members flex">
                  <div style={{ margin: "5px" }}>
                    <img src={personAdd} alt="person add" />
                  </div>
                  <div>
                    <img src={groupAdd} alt="group add" />
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="container-chat">
            <div className="container-controll flex">
              <div className="left-controll flex">
                <p onClick={() => handleShowAllMessage(true)}>Tất cả</p>
                <p onClick={() => handleShowAllMessage(false)}>Chưa đọc</p>
              </div>
              <div className="right-controll flex">
                <div className="classification-more flex">
                  <p>Phân loại</p>
                  <img src={expandMore} alt="classification more" />
                </div>
                <div className="btn-more">
                  <img src={more} alt="them" />
                </div>
              </div>
              <hr
                className={`hr-transaction ${
                  showAllMessgae ? "" : "hr-active"
                }`}
              />
            </div>
            <div className="list-chat">
              <div className="flex">
                <div></div>
              </div>
            </div>
          </div>
        </div>
        <div className="right-bar"></div>
      </div>
    </>
  );
}

export default Message;
